// Node module: apiconnect-apigateway-framework

'use strict';

const Path = require('path');
const fs = require('fs');

const FRAMEWORK_PROMISE = new Promise((resolve, reject) => {
  let filepath = Path.resolve(__dirname, 'apigateway.zip');
  fs.readFile(filepath, (err, data) => {
    if (err)
      return reject(err);
    resolve(data);
  });
});

module.exports = () => FRAMEWORK_PROMISE.then(buffer => buffer);
