// Node module: apiconnect-apigateway-framework

'use strict';

const assert = require('assert');
const apigw = require('../');

describe('API Gateway Framework', function () {
  it('should successfully load', function () {
    return apigw();
  });
  it('should be a buffer', function () {
    return apigw().then(buffer => {
      assert.ok(buffer instanceof Buffer, 'Expected `buffer instanceof Buffer` to be true');
    });
  });
});
