// Node module: apiconnect-apim-client

'use strict';

var _ = require('lodash');
var f = require('util').format;
var reqOpts = require('./req-options');
var request = require('./request');
var validator = require('./opts-validator');

function list(options) {
  options = options || {};
  return validator(options, [ 'organization' ]).then(function(options) {
    options = reqOpts(_.defaults({
      url: f(
        '/v1/users/context/%s',
        !options.catalog ? 'apimanager' : options.organization + '.' + options.catalog
      ),
      json: true,
    }, options));
    return request(options);
  });
};

function get(options) {
  options = options || {};
  return validator(options, [ 'organization', 'userName' ]).then(function(options) {
    options = reqOpts(_.defaults({
      url: f(
        '/v1/users/context/%s/%s',
        !options.catalog ? 'apimanager' : options.organization + '.' + options.catalog,
        options.userName
      ),
      json: true,
    }, options));
  });
};

module.exports = {
  list: list,
  get: get,
};

