// Node module: apiconnect-assembly

'use strict';

var gulp = require('gulp');
var copy = require('gulp-copy');

gulp.task('copy', ['clean'], function () {
  return gulp.src([
	  'src/css/*.css'
    ])
    .pipe(copy('dist/', {prefix: 2}));
});

gulp.task('copy:apimui', function () {
  return gulp.src([
	  'dist/apiconnect-assembly.js'
    ])
    .pipe(copy('../apim-ui/app/assembly/', {prefix: 1}));
});

