// Node module: apiconnect-assembly

'use strict';

var gulp = require('gulp');

gulp.task('watch:dist', function () {
  return gulp.watch(['dist/**'], ['copy:apimui']);
});

gulp.task('watch:src', function () {
 gulp.watch(['src/html/**', 'src/css/**', 'src/js/**'], ['build']);
 return;
});

gulp.task('watch:js', function () {
	return gulp.watch(['app/**/*.js', '!app/bower_components/**/*.js'], ['jshint']);
});

// todo add watch:test to task list
gulp.task('watch', ['watch:src', 'watch:dist']);

