// Node module: apiconnect-cli-apps


var _ = require('lodash');
var logger = require('apiconnect-cli-logger');

function format(result) {
  _.each(result, function(app) {
    logger.writeln(app.fullId);
  });
};

function error(error) {
  throw error;
};

module.exports = {
  error: error,
  format: format,
};

