// Node module: apiconnect-cli-auth


// Note: This module delays all require statements as it will directly impact
// cli load times.

var SG = require('strong-globalize');
SG.SetRootDir(__dirname);

exports.getTopics = function() {
  var plugin = require('./lib/plugins-impl');
  return plugin.topics();
};

exports.getComponents = function() {
  var plugin = require('./lib/plugins-impl');
  return plugin.components();
};

exports.getCredentials = function(opts) {
  var auth = require('./lib/auth');
  return auth.getCredentials(opts);
};

exports.login = function(username, password, opts) {
  var login = require('./lib/login');
  return login.login(username, password, opts);
};

/**
 * Login to a bluemix server only
 * @param {String} hostname
 * @param {object} [options]
 * @param {String} [options.code]
 * @param {String} [options.passcode]
 * @param {boolean} [options.interactiveMode] Prompt for passcode if needed. If false, code or passcode must be
 * provided. Defaults true.
 */
exports.bluemixLogin = function(hostname, opts) {
  var bluemixLogin = require('./lib/bluemix-login');
  return bluemixLogin.login(hostname, opts);
};

exports.logout = function(opts) {
  var logout = require('./lib/logout');
  return logout.logout(opts);
};

