// Node module: apiconnect-cli-auth

var config = require('apiconnect-config').loadConfig();
var g = require('strong-globalize')();

var DEFAULT_APIM_SERVER = 'us.apiconnect.ibmcloud.com';
exports.DEFAULT_APIM_SERVER = DEFAULT_APIM_SERVER;

function resolveHostname(options) {
  var serverConfig = null;
  if (options.uri) {
    serverConfig = config.parseUriValue(options.uri);
  } else {
    serverConfig = config.getOne('org') || { data: {} };
  }

  if (options.useDefaultRegion) {
    serverConfig.host = serverConfig.host || DEFAULT_APIM_SERVER;
  }

  if (!serverConfig.hostname) {
    throw new Error(g.f('The {{--server}} option is required.'));
  }
  return serverConfig.host;
}
module.exports = {
  DEFAULT_APIM_SERVER: DEFAULT_APIM_SERVER,
  resolveHostname: resolveHostname,
};

