// Node module: apiconnect-cli-components


var _ = require('lodash');
var g = require('strong-globalize')();
var logger = require('apiconnect-cli-logger');

module.exports.listFormat = function(components, options) {
  _.keys(components).forEach(function(key) {

    var comp = components[key];

    var orgName = options.organization;

    logger.writeln(g.f('%s in %s',
      comp.info.name, orgName));
  });
};

