// Node module: apiconnect-cli-components

var g = require('strong-globalize')();

module.exports = {
  name: 'components',
  version: require('../package.json').version,
  path: __filename,
  isBuiltin: true,
  topics: getTopics,
  componentsPublish: function() {
    return require('./plugin-impl').componentsPublish.apply(this, arguments);
  },
};

function getTopics() {
  return [ {
    name: 'components',
    commands: [
      {
        command: 'list',
        aliases: [ 'components' ],
        loginRequired: 'org',
        helpInfo: g.f('apic_components_list_help.txt'),
        action: function(opts) {
          return require('./plugin-impl').componentsList(opts);
        },
        options: [
          { name: 'organization', rune: 'o', type: 'name', default: '$APIC_CONFIG$.catalog.data.orgs' },
          { name: 'all-organizations', rune: 'O', type: 'boolean' },
          { name: 'server', rune: 's', type: 'host', default: '$APIC_CONFIG$.catalog.host', required: true },
        ],
      },
    ],
  },
  ];
};

