// Node module: apiconnect-cli-components


var ComponentsAPI = require('apiconnect-apim-client').components;
var fmt = require('./formatter');

module.exports = {
  componentsList: componentsList,
};

/**
 * List all components
 *
 * @param options            flag options
 */
function componentsList(options) {
  if (options.allOrganizations) {
    options.organization = null;
  }
  return ComponentsAPI.list(options)
    .then(function(components) { fmt.listFormat(components, options); });
};

