// Node module: apiconnect-cli-config

var basicPluginTest = require('apiconnect-cli-test-support').basicPluginTest;
var path = require('path');
var test = require('tap').test;

test('Smoke test', function(t) {
  basicPluginTest(t, path.dirname(__dirname), {
    config: [
      'list',
      'get <name>',
      'set <name=value> [name=value...]',
      'delete <name>',
      'clear',
    ],
  });

  t.end();
});

