// Node module: apiconnect-cli-policies


var devapps = require('apiconnect-apim-client').devapps;
var Formatter = require('./formatter');

function devappsGet(devAppName, options) {
  var fmt = new Formatter();
  options.name = devAppName;
  return devapps.get(options).then(function(result) {
    fmt.getFormatter(result, options);
  });
}

function devappsList(options) {
  var fmt = new Formatter();

  return devapps.list(options).then(function(result) {
    fmt.listFormatter(result, options);
  });
};

module.exports.devappsGet = devappsGet;
module.exports.devappsList = devappsList;

