// Node module: apiconnect-cli-policies

var _ = require('lodash');
var g = require('strong-globalize')();
var logger = require('apiconnect-cli-logger');

module.exports.listFormat = function(results, options) {
  var i, roles, org;
  switch (options.scope) {
    case 'org':
    case 'consumer-org':
      for (i = 0; i < results.length; i++) {
        roles = _.map(results[i].roles, 'name').toString();
        if (options.consumerOrg) {
          org = options.consumerOrg;
        } else {
          org = options.organization;
        }
        logger.writeln(g.f('User %s (User ID: %s) having %s role in %s org',
          results[i].user.name, results[i].user.id, roles, org));
      }
      break;
    default:
      throw new Error(g.f('Invalid scope %s, valid scopes are "org" and "consumerorg" ', options.scope));
  }
};

