// Node module: apiconnect-cli-policies


var listFormat = require('./formatter').listFormat;
var members = require('apiconnect-apim-client').members;

module.exports.list = list;
function list(options) {
  if (!options.scope) {
    // default to org
    options.scope = 'org';
  }

  return members.list(options).then(function(result) {
    listFormat(result, options);
  });
}

