// Node module: apiconnect-cli-policies


var _ = require('lodash');
var g = require('strong-globalize')();
var logger = require('apiconnect-cli-logger');

exports.getFormatter = function(result, options) {
  if (options.type && options.type === 'consumer') {
    var ownerDefault = {
      firstName: g.f('unknown'),
      lastName: g.f('unknown'),
      email: g.f('unknown'),
      username: g.f('unknown'),
    };
    result.owner = _.extend(ownerDefault, result.owner);
    logger.writeln(g.f('name: %s', result.name));
    logger.writeln(g.f('display name: %s', result.displayName));
    logger.writeln(g.f('owner name: %s %s', result.owner.firstName, result.owner.lastName));
    logger.writeln(g.f('owner email: %s', result.owner.email));
    logger.writeln(g.f('owner username: %s', result.owner.username));
    logger.writeln(g.f('status: %s', result.status));
    if (result.tags) {
      logger.writeln(g.f('communities: %s', result.tags.toString(result.tags)));
    }
  } else {
    logger.writeln(g.f('name: %s', result.name));
    logger.writeln(g.f('display name: %s', result.displayName));
    logger.writeln(g.f('status: %s', result.status));
  }
};

exports.listFormatter = function(results) {
  results.forEach(function(org) {
    logger.writeln(org.name);
  });
};

exports.error = function(error) {
  throw error;
};

