// Node module: apiconnect-cli-orgs

/*eslint brace-style: 0, max-len: 0*/

var orgs = require('apiconnect-apim-client').organizations;
var fmt = require('./formatter');

module.exports.orgsGet = orgsGet;
function orgsGet(name, options) {
  options.name = name;
  return orgs.get(options).then(function(result) {
    fmt.getFormatter(result, options);
  });
}

module.exports.orgsList = orgsList;
function orgsList(options) {
  return orgs.list(options).then(function(result) {
    fmt.listFormatter(result, options);
  });
};

