// Node module: apiconnect

/**
 * Loads the help text for the given appName from the file system.
 * @param {string} appName - The application name. (ex. apic)
 * @param {array} dirs - An array of directories (path strings).
 * @param {string} pkgName - The package name. (ex. apiconnect)
 * @returns {string}
 */
var g = require('strong-globalize')();

module.exports = function(appName) {
  return g.f(appName + '_help.txt');
};

