// Node module: apiconnect-cli-policies

var g = require('strong-globalize')();
var logger = require('apiconnect-cli-logger');

exports.listFormatter = function(gateways) {
  gateways.forEach(function(gateway) {
    logger.writeln(g.f('%s - %s', gateway._id, gateway.desc));
  });
};

exports.createFormatter = function(gateway, options) {
  logger.writeln(g.f('Created secure gateway %s in %s', gateway._id, options.organization));
};

exports.getFormatter = function(result, options) {
  logger.writeln(g.f('name: %s', result.desc));
  logger.writeln(g.f('organization: %s', options.organization));
  logger.writeln(g.f('status: %s', result.status.toLowerCase()));
  logger.writeln(g.f('active clients: %s', result.connectedClientsArr.length));
  logger.writeln(g.f('id: %s', result._id));
  logger.writeln(g.f('token: %s', result.jwt));
};

exports.deleteFormatter = function(gatewayId, options) {
  logger.writeln(g.f('Deleted secure gateway %s in %s', gatewayId, options.organization));
};

exports.setFormatter = function(gatewayId, options) {
  logger.writeln(g.f('Updated secure gateway %s in %s', gatewayId, options.organization));
};

exports.error = function(error) {
  throw error;
};

