// Node module: apiconnect-cli-securegateways

var secureGateways = require('apiconnect-apim-client').securegateways;
var fmt = require('./formatter');

function listGateways(options) {
  return secureGateways.list(options).then(function(gateways) {
    fmt.listFormatter(gateways);
  });
};

function createGateway(options) {
  return secureGateways.create(options).then(function(gateway) {
    fmt.createFormatter(gateway, options);
  });
}

function getGateway(gatewayId, options) {
  options.gateway = gatewayId;

  return secureGateways.get(options).then(function(result) {
    fmt.getFormatter(result, options);
  });
}

function deleteGateway(gatewayId, options) {
  options.gateway = gatewayId;

  return secureGateways.delete(options).then(function() {
    fmt.deleteFormatter(gatewayId, options);
  });
}

function setGateway(gatewayId, options) {
  options.gateway = gatewayId;

  return secureGateways.set(options).then(function() {
    fmt.setFormatter(gatewayId, options);
  });
}

module.exports = {
  listGateways: listGateways,
  createGateway: createGateway,
  getGateway: getGateway,
  deleteGateway: deleteGateway,
  setGateway: setGateway,
};

