// Node module: apiconnect-cli-validate

/*eslint brace-style: 0, max-len: 0*/

var g = require('strong-globalize')();

module.exports = {
  name: 'local',
  version: require('../package.json').version,
  path: __filename,
  isBuiltin: true,
  topics: topics,
};

function topics() {
  var validateCmd = {
    command: 'validate <filename>',
    aliases: [ 'validate' ],
    helpInfo: function() {
      return g.f('apic_local_validate_help.txt');
    },
    action: require('./actions').validate,
    options: [
      '-p, --product-only',
      '--no-extensions',
    ],
  };

  return [ {
    name: 'local',
    description: 'Validate a product and/or a swagger api',
    commands: [ validateCmd ],
  } ];
}

