'use strict';

module.exports = Payload;

/**
 *
 * @param {string} path - The path to the resource.
 * @param {string} verb  - The REST verb of the request.
 * @param {object} data  - The JMX body of the request. Must be a JSON object,
 * not a string!
 * @param {string=} qs - An optional query string to apply to the payload.
 * @constructor
 */
function Payload(path, verb, data, qs) {
  this.path = path;
  this.verb = verb;
  this.data = data;
  this.qs = qs; // Optional
}
