'use strict'

const format = require('util').format;

const logger = (function () {
  let writeLog;
 
  return {
    setWriteLog: function (writeLog_arg) {
      writeLog = writeLog_arg;
    },

    writeToLog: function () {
      let msg = format.apply(null, arguments);
      writeLog(msg);
    },

    getWriteLog: function () {
      return writeLog;
    }
  };
  
})();

exports.logger = logger;
