'use strict';

const os = require('os');

const PLATFORM_CODE = (() => {
  let platform = os.platform();
  if (platform !== 'win32')
    return platform;
  let release = os.release();
  let major   = parseInt(release.split('.')[0]);
  return `win${major < 10 ? '7' : '10'}`
})();

const PLATFORM = (() => {
  let platform;
  switch(PLATFORM_CODE) {
    case 'darwin':
      platform = require('./darwin');
      break;
    case 'linux':
      platform = require('./linux');
      break;
    case 'win7':
      platform = require('./win7');
      break;
    case 'win10':
      platform = require('./win10');
      break;
    default:
      platform = require('./linux');
      break;
  }
  return platform;
})();

module.exports = () => PLATFORM;
