// Node module: apiconnect-mgmt-lite-datapower

'use strict';

const Id2Path = new Map();
const Path2Id = new Map();

exports.addPath = (objId, objPath) => {
  Id2Path.set(objId, objPath);
  Path2Id.set(objPath, objId);
};

exports.clearPaths = () => {
  Id2Path.clear();
  Path2Id.clear();
};

exports.getPath = objId => {
  return Id2Path.get(objId);
};

exports.getId = objPath => {
  return Path2Id.get(objPath);
};

exports.realName = obj => {
  let d = obj.document;
  return d.info && (d.info['x-ibm-name'] || d.info.name);
};

exports.nameVers = obj => {
  let name = exports.realName(obj);
  let vers = obj.document.info && obj.document.info.version;
  return `${name}:${vers}`;
};

