// Node module: apiconnect-mgmt-lite-datapower

'use strict';
/* eslint-disable max-len */
const Crypto = require('crypto');
const genid = n => Crypto.randomBytes(n || 25).toString('hex').slice(0, n || 25);
const hash = d => Crypto.createHash('sha256').update(d).digest('hex').slice(0, 25);

module.exports = genid;
module.exports.hash = hash;

