// Node module: apiconnect-mgmt-lite-datapower

'use strict';

const LOG_LEVELS = {
  debug  : 1,
  info   : 2,
  notice : 2,
  warn   : 3,
  warning: 3,
  error  : 4,
  severe : 4,
  fatal  : 5
};

exports.LOG_LEVELS = LOG_LEVELS;

exports.LOG_LEVEL = (() => {
  let env = process.env;
  let level = env.LOG_LEVEL                          ||
              env.FILE_LOG_LEVEL                     ||
              env.APIC_LOG_CONSOLE_LEVEL             ||
              env.IBM_APICONNECT_APIM_LITE_LOG_LEVEL ||
              'info';
  return LOG_LEVELS[level] || 3;
})();

