// Node module: apiconnect-mgmt-lite-datapower

'use strict';

const fs = require('fs');
if (process.env.npm_config_registry) {
  try {
    fs.writeFileSync('.env', `IBM_APICONNECT_NPM_REGISTRY=${process.env.npm_config_registry}\n`);
  } catch (err) {
    console.log(`Failed to save ${process.env.npm_config_registry} in .env!`);
  }
}

