// Node module: apiconnect-cli-pm

'use strict';

var apicConf = require('apiconnect-config');
var path = require('path');

var conf = {};
conf.startTimeout = process.env.APIC_START_TIMEOUT || 20000;
conf.pmPort = process.env.APIC_SERVICE_MANAGER_PORT || 8702;
conf.pmHome = process.env.APIC_SERVICE_MANAGER_HOME
  || path.resolve(apicConf.getUserConfigDir(), 'strong-pm');
conf.pmBasePort = process.env.APIC_SERVICE_BASE_PORT || 4000;

module.exports.get = function(key) {
  return conf[key];
};

