// Node module: apim-ui

'use strict';

self.validateWorker = require('apiconnect-validate');
self.validateWorker.validateAPI(self.api, self.vendorExtensions).then(function(results) {

  var message = {
    type: 'RESULT',
    results: results,
  }
  self.postMessage(message);

}, function (err) {

  var message = {
    type: 'ERROR',
    results: err,
  }
  self.postMessage(message);

});
