// Node module: apim-ui

'use strict';

angular.module('apim.account')
  .controller('AccountAgreementController', [
    '$scope',
    '$modalInstance',
    '$localStorage',
    'AnalyticsServices',
    AccountAgreementController
  ]);

function AccountAgreementController(
  $scope,
  $modalInstance,
  $localStorage,
  AnalyticsServices
) {
  $scope.closeModal = $modalInstance.close;

  $scope.agree = function(){
    if ($scope.triggerChat){
      $localStorage.setItem('triggerChat', true);
    } else {
      $localStorage.removeItem('triggerChat');
    }

    $scope.closeModal();
    AnalyticsServices.setEnableAnalytics(true, $scope);
  };

  $scope.cancel = function(){
    $scope.closeModal();
    $scope.settings.analytics = false;
  };
}

