// Node module: apim-ui

'use strict';


angular.module('apim.account')
  .directive('apimAnalyticsOptin', ['$log', function($log) {
    return {
      restrict: 'E',
      scope: {
        closeModal: '&'
      },
      templateUrl: 'apim/account/partials/analytics-optin.html',
      controller: function($scope, $rootScope, $state, $timeout) {
        $scope.close = function(){
          $scope.closeModal();
          $timeout(function(){
            $state.go('account.settings');
          }, 500);
        }
      }
    }
  }])
  .directive('apimSignout', [
    '$rootScope',
    function(
      $rootScope
    ) {
      return {
        restrict: 'A',
        scope: {},
        link: function(scope, el, attr) {
          el.on('click', function(event){
            event.stopPropagation();
            $rootScope.$emit('event:signOut');
          });
        }
      }
    }]);

