// Node module: apim-ui

'use strict';

angular.module('apim.account')
  .controller('AccountSettingsController', [
    '$scope',
    '$rootScope',
    'AnalyticsServices',
    AccountSettingsController
  ]);

function AccountSettingsController(
  $scope,
  $rootScope,
  AnalyticsServices
) {
  $scope.settings = {
    analytics: $rootScope.analytics
  };

  $scope.setEnableAnalytics = function(skipAgreement){
    return AnalyticsServices.setEnableAnalytics(skipAgreement, $scope);
  };

  $scope.signOut = function(){
    $rootScope.$emit('event:signOut');
  };
}

