// Node module: apim-ui

'use strict';

/* Services */

var apps = angular.module('apim.apps');

apps.factory('App', ['$resource',
  function($resource){
    return $resource('proxy/orgs/:orgId/environments/:envId/:spaces/:spaceId/consumerApps', {}, {
      query: {method:'GET', params:{orgId: '@orgId', envId: '@envId', consumerOrg: '@consumerOrg'}, isArray:true}
    });
  }]);

apps.factory('ProviderApp', ['$resource',
  function($resource){
    return $resource('proxy/orgs/:orgId/environments/:envId/:spaces/:spaceId/providerApps', {}, {
      query: {method:'GET', params:{orgId: '@orgId', envId: '@envId'}, isArray:true},
      save: {method: 'POST', params: {orgId: '@orgId', envId: '@envId'}, isArray: false}
    });
  }]);

apps.factory('ProviderAppSubscription', ['$resource',
  function($resource){
    return $resource('proxy/orgs/:orgId/environments/:envId/:spaces/:spaceId/providerApps/:planId/subscriptions/:subscriptionId', {}, {
      query: {method:'GET', params:{orgId: '@orgId', envId: '@envId', consumerOrg: '@consumerOrg'}, isArray:true}
    });
  }]);

apps.factory('AppSubscription', ['$resource',
  function($resource){
    return $resource('proxy/orgs/:orgId/environments/:envId/:spaces/:spaceId/plans/:planId/apps', {}, {
      query: {method:'GET', params:{orgId: '@orgId', envId: '@envId', consumerOrg: '@consumerOrg'}, isArray:true}
    });
  }]);

apps.factory('Subscription', ['$resource',
  function($resource){
    return $resource('proxy/orgs/:orgId/environments/:envId/:spaces/:spaceId/consumerApps/:appId/subscriptions/:subscriptionId', {}, {
      query: {method:'GET', params:{orgId: '@orgId', envId: '@envId', appId: '@appId'}, isArray:true}
    });
  }]);

