'use strict';

/*
  * Usage:
  * <bx-loading data-is-done-loading="doneLoading"></bx-loading>
  *
  * Attributes:
  * <bx-loading>:
  *    1. 'data-is-done-loading': needs to be set to true when loading circle needs to be done.
  */

  angular.module('bluemix.components')
  .directive('bxLoading', ['$http', function($http) {
    return {
      restrict: 'E',
      templateUrl: 'apim/bluemix/components/loading/loading.html',
      scope: {
        isDoneLoading: '='
      }
    };
  }]);

