// Node module: apim-ui

'use strict';

/* Filters */

var catalogs = angular.module('apim.catalogs');

catalogs.filter('entityType', function() {
  return function (entities, filteredTypes) {
    if(!entities) return entities;
    if(!filteredTypes) return filteredTypes;
    return entities.filter(function(entity) {
      return filteredTypes[entity.type];
    });
  };
});

