// Node module: apim-ui

'use strict';

angular.module('apim.components').directive('apicComponentEditor', function() {
  return {
    restrict: 'E',
    templateUrl: 'apim/components/partials/component-editor.html'
  };
}).directive('apicDraftComponent', function() {
  return {
    restrict: 'E',
    templateUrl: 'apim/components/partials/draft-component.html'
  };
}).directive('apicComponentMover', function () {
  return {
    restrict: 'E',
    templateUrl: 'apim/components/partials/component-mover.html',
    scope: {
      referenceSource: '=',
      referenceScope: '@',
      emptyMessage: '&'
    },
    controller: "ComponentSelectorController",
    controllerAs: "componentCtrl"
  };
}).directive('apicComponentReference', function () {
  return {
    restrict: 'E',
    templateUrl: 'apim/components/partials/component-reference.html',
    scope: {
      referenceSource: '=',
      referenceScope: '@'
    },
    controller: "ComponentReferenceController",
    controllerAs: "componentCtrl"
  };
});

