// Node module: apim-ui

'use strict';

/* Controllers used by both APIM-UI and CMC-UI */

var common = angular.module('apim.common');

common.controller('ConfirmationController', ['$scope', '$modalInstance', confirmationController]);

function confirmationController($scope, $modalInstance) {

  $scope.confirmation.positiveAction = $scope.confirmation.positiveAction || 'ok';
  $scope.confirmation.negativeAction = $scope.confirmation.negativeAction || 'cancel';

  $scope.confirm = function() {
    $scope.confirmation.confirmed();
    $modalInstance.close();
  };
  $scope.reject = function() {
    $scope.confirmation.rejected();
    $modalInstance.close();
  };
};

