// Node module: apim-ui

'use strict';

/* Controllers */

var deployment = angular.module('apim.deployment', ['ngResource', 'apim.securegateways']);

deployment.controller('AvailabilityViewController', ['$scope', availabilityViewController]);

function availabilityViewController($scope) {
  $scope.deleteTag = function(tag) {
    if (tag.org) {
      $scope.$parent.selectedOrgsVisible = $scope.$parent.selectedOrgsVisible.filter(function(orgTag) {
        return (orgTag.value != tag.value);
      });
    } else if (tag.community) {
      $scope.$parent.selectedCommunitiesVisible = $scope.$parent.selectedCommunitiesVisible.filter(function(communityTag) {
        return (communityTag.value != tag.value);
      });
    }
  };
};

deployment.controller('AvailabilitySubscribeController', ['$scope', availabilitySubscribeController]);

function availabilitySubscribeController($scope) {
  $scope.deleteTag = $scope.deleteTagSubscribable;
};

