// Node module: apim-ui

'use strict';

/* Services */

var developers = angular.module('apim.developers');

developers.factory('DeveloperOrganization', ['$resource',
  function($resource){
    return $resource('proxy/orgs/:orgId/:environments/:envId/:spaces/:spaceId/:consumerOrgs/:changeOwner/:newOwnerId', {}, {
      query: {method:'GET', params:{orgId: '@orgId', envId: '@envId', environments: 'environments', consumerOrgs: 'consumerOrgs'}, isArray: true},
      save: {method:'PUT', params:{orgId: '@orgId'}, isArray: false,
        transformRequest: function(data, headersGetter) {
          return JSON.stringify({name: data.name, displayName: data.displayName});
        }},
      changeOwner: {method:'POST', params:{orgId: '@orgId', changeOwner: 'changeOwner', newOwnerId: '@newOwnerId'}, isArray: false,
        transformRequest: function(data, headersGetter) {
          return null;
        }}
    });
  }]);

developers.factory('DeveloperMessage', ['$resource',
  function($resource){
    return $resource('proxy/orgs/:orgId/communications', {}, {
      send: {method:'POST', params:{orgId: '@orgId'}, isArray: false}
    });
  }
]);

