'use strict';

/* Services */

var environments = angular.module('apim.environments');

environments.factory('Environment', ['$resource',
  function($resource){
    return $resource('proxy/orgs/:orgId/environments/:envId', {}, {
      query: {method:'GET', params:{orgId: '@orgId', includeSpaces: true}, isArray:true},
      update: {method:'PUT', params:{orgId: '@orgId', envId: '@envId', includeSpaces: true}},
      save: {method:'POST', params:{orgId: '@orgId', includeSpaces: true}},
      remove: {method:'DELETE', params:{orgId: '@orgId', envId: '@envId'}}
    });
  }]);

environments.factory('VendorExtension', ['$resource',
  function($resource){
    return $resource('proxy/orgs/:orgId/environments/:envId/extensions/:extId', {}, {
      query: {method:'GET', params:{orgId: '@orgId',envId: '@envId'}, isArray:true},
      queryAll: {method: 'GET', params:{orgId: '@orgId'}, isArray:true},
      update: {method:'PUT', params:{orgId: '@orgId', envId: '@envId', extId: '@extId'}},
      save: {method:'POST', params:{orgId: '@orgId', envId: '@envId'}},
      remove: {method:'DELETE', params:{orgId: '@orgId', envId: '@envId', extId: '@extId'}}
    });
  }]);

environments.factory('GatewayVendorExtension', ['$resource',
  function($resource){
    return $resource('proxy/orgs/:orgId/environments/:envId/gw-services/:gwServiceId/extensions/:extId', {}, {
      query: {method:'GET', params:{orgId: '@orgId',envId: '@envId', 'gwServiceId': '@gwServiceId'}, isArray:true},
      queryAll: {method: 'GET', params:{orgId: '@orgId', 'gwServiceId': '@gwServiceId'}, isArray:true},
      update: {method:'PUT', params:{orgId: '@orgId', envId: '@envId', 'gwServiceId': '@gwServiceId', extId: '@extId'}},
      save: {method:'POST', params:{orgId: '@orgId', envId: '@envId', 'gwServiceId': '@gwServiceId'}},
      remove: {method:'DELETE', params:{orgId: '@orgId', envId: '@envId', 'gwServiceId': '@gwServiceId', extId: '@extId'}}
    });
  }]);


environments.factory('GatewayCluster', ['$resource',
  function($resource){
    return $resource('proxy/gatewayClusters', {}, {
      query: {method:'GET', params:{}, isArray:true}
    });
  }]);

environments.factory('EnvRole', ['$resource',
  function($resource) {
    return $resource('proxy/orgs/:orgId/environments/:envId/:spaces/:spaceId/roles/:roleId', {}, {
      query: {method:'GET', params: {orgId: '@orgId', envId: '@envId'}, isArray: true},
      update: {method:'PUT', params: {orgId: '@orgId', envId: '@envId', roleId: '@roleId'},
        transformRequest: function(data, headersGetter) {
          return JSON.stringify({permissions: data.permissions});
        }
      }
    });
  }]);

environments.factory('Space', ['$resource',
  function($resource) {
    return $resource('proxy/orgs/:orgId/environments/:envId/spaces/:spaceId/:transferOwner', {}, {
      query: {method:'GET', params: {orgId: '@orgId', envId: '@envId'}, isArray: true},
      update: {method:'PUT', params: {orgId: '@orgId', envId: '@envId', spaceId: '@spaceId'}},
      save: {method:'POST', params: {orgId: '@orgId', envId: '@envId'}},
      remove: {method:'DELETE', params: {orgId: '@orgId', envId: '@envId', spaceId: '@spaceId'}},
      transferOwner: {method:'POST', params: {orgId: '@orgId', envId: '@envId', spaceId: '@spaceId', transferOwner: 'transferOwner'}}
    });
  }]);

