// Node module: apim-ui

'use strict';

/* Wrapping translation filters */

  angular.module('apim.core').filter('translateMultiple', ['translateFilter', function(translateFilter) {
    return function (property, values) {
      return translateFilter(property, values);
    };
  }]);

angular.module('apim.core').filter('translatePlurality', ['translateFilter', function(translateFilter) {
  return function (value, property, substituteVar) {
    var substitutions = {};
    if (!substituteVar) substituteVar = "0";
    substitutions[substituteVar] = value;
    if (value == 1) {
      return translateFilter(property + "Singular", substitutions);
    } else {
      return translateFilter(property + "Plural", substitutions);
    }
  };
}]);

