// Node module: apim-ui

'use strict';

angular.module('apim.log')
  .service('LogServices', [
    '$log',
    LogServices]);

function LogServices($log){
  var svc = {};

  svc.getLogLines = function(raw){
    var lines = [];
    var logs = [];

    // split newline into multiple lines
    if (raw.data) {
      lines = lines.concat(raw.data.toString().split('\n'));
    } else {
      lines = lines.concat(raw.toString().split('\n'));
    }

    lines.forEach(function(line){
      var htm = ansi_up.ansi_to_html(line, { use_classes: true });

      logs.push(htm);
      $log.log(line);
    });

    return logs;
  };

  return svc;
}

