// Node module: apim-ui

'use strict';

function getDefaultDataSource(dataSources) {
  var defaultName = 'db';
  var isDefault = function(ds) {
    return ds.name === defaultName;
  };

  return dataSources.some(isDefault) ? defaultName : null;
};

angular.module('apim.models')
  .service('ModelServices', [
    'ModelDefinition',
    'ModelConfig',
    function ModelServices(ModelDefinition, ModelConfig) {
      this.create = function(name, dataSources, modelConfig) {
        return ModelDefinition.create({
          facetName: 'common',
          base: 'PersistedModel',
          name: name
        }).$promise
          .then(function(res) {
            return ModelConfig.create({
              name: res.name,
              facetName: 'server',
              public: true,
              dataSource: getDefaultDataSource(dataSources)
            })
          })
      };
    }
  ]);

