// Node module: apim-ui

'use strict';

/* Services */

var notifications = angular.module('apim.notifications');

notifications.factory('Notifications', ['$resource',
  function($resource){
    return $resource('proxy/orgs/:orgId/notifications', {}, {
      query: {method:'GET', params:{orgId: '@orgId'}, isArray: true}
    });
  }]);

notifications.factory('CMCNotifications', ['$resource',
  function($resource){
    return $resource('proxy/cloud/notifications', {}, {
      query: {method:'GET', isArray: true}
    });
  }]);

