// Node module: apim-ui

'use strict';

/* Filters */

var objects = angular.module('apim.objects');

objects.filter('standardObjectQuery', function() {
  return function (objects, query) {
    if(!objects || !query) return objects;
    var term = query.toLowerCase();
    return objects.filter(function(object) {
      if (object.info && object.info.name && object.info.name.toLowerCase().indexOf(term) > -1) return true;
      if (object.info && object.info.version && object.info.version.toLowerCase().indexOf(term) > -1) return true;
      if (object.info && object.info.title && object.info.title.toLowerCase().indexOf(term) > -1) return true;
      if (object.info && object.info.description && object.info.description.toLowerCase().indexOf(term) > -1) return true;
      return false;
    });
  };
}).filter('tlsProfileQuery', function() {
  return function (objects, query) {
    if(!objects || !query) return objects;
    var term = query.toLowerCase();
    return objects.filter(function(object) {
      if (object.name && object.name.toLowerCase().indexOf(term) > -1) return true;
      if (object.title && object.title.toLowerCase().indexOf(term) > -1) return true;
      if (object.description && object.description.toLowerCase().indexOf(term) > -1) return true;
      return false;
    });
  };
}).filter('oauthProviderQuery', function() {
  return function (objects, query) {
    if(!objects || !query) return objects;
    var term = query.toLowerCase();
    return objects.filter(function(object) {
      if ((object.name && object.name.toLowerCase().indexOf(term) > -1) ||
         (object.title && object.title.toLowerCase().indexOf(term) > -1) ||
         (object.description && object.description.toLowerCase().indexOf(term) > -1)) return true;
      return false;
    });
  };
}).filter('userRegistryQuery', function() {
  return function (objects, query) {
    if(!objects || !query) return objects;
    var term = query.toLowerCase();
    return objects.filter(function(object) {
      if (object.name && object.name.toLowerCase().indexOf(term) > -1) return true;
      if (object.title && object.title.toLowerCase().indexOf(term) > -1) return true;
      if (object.description && object.description.toLowerCase().indexOf(term) > -1) return true;
      if (object.$$type && object.$$type.toLowerCase().indexOf(term) > -1) return true;
      return false;
    });
  };
});

