// Node module: apim-ui

'use strict';

/* Services */

  angular.module('apim')
  .factory('StagingTarget', ['$resource', function($resource){
    return $resource('proxy/orgs/:orgId/targets/:targetId/', {}, {
      query: {
        method: 'GET',
        params: {
          orgId: '@orgId'
        },
        isArray: true
      },
      get: {
        method: 'GET',
        params: {
          orgId: '@orgId',
          targetId: '@targetId'
        },
        isArray: false
      },
      create: {
        method: 'POST',
        params: {
          orgId: '@orgId'
        },
        isArray: false
      },
      update: {
        method: 'PUT',
        params: {
          orgId: '@orgId'
        },
        isArray: false
      },
      delete: {
        method: 'DELETE',
        params: {
          orgId: '@orgId',
          targetId: '@targetId'
        },
        isArray: false
      }
    });
  }
  ]);

