// Node module: apim-ui

'use strict';

/* Directives */

var secureGateways = angular.module('apim.securegateways');

secureGateways.directive('apimHostsAndPaths', function() {
  return {
    restrict: 'E',
    templateUrl: 'apim/securegateways/partials/hostsAndPaths.html',
    scope: {
      hosts: "=hostList"
    },
    controller: function($scope) {
      $scope.collapsedHosts = {};

      $scope.closeHost = function($event, name) {
        $scope.collapsedHosts[name] = !$scope.collapsedHosts[name];
      };
    }
  };
});

secureGateways.directive('apimSecureGateway', function() {
  return {
    restrict: 'E',
    templateUrl: 'apim/securegateways/partials/secureGateway.html'
  };
});

secureGateways.directive('apimSecureGatewayGuidedTour', function(){
  return {
    restrict: 'E',
    templateUrl: 'apim/securegateways/partials/guidedTour.html'
  }
});

