// Node module: apim-ui

'use strict';

/* Services */

  angular.module('apim')
  .factory('APIc', ['$resource', function($resource) {
    var returnedObj = {};

    returnedObj.orgs = $resource('apic/orgs', {}, {
      query: {
        method: 'GET',
        isArray: true
      }
    });

    returnedObj.catalogs = $resource('/apic/orgs/:orgId/catalogs', {}, {
      query: {
        method: 'GET',
        params: {
          orgId: '@orgId'
        },
        isArray: true
      }
    });

    returnedObj.apps = $resource('/apic/instances/:instance/orgs/:orgId/providerApps', {}, {
      query: {
        method: 'GET',
        params: {
          instance: '@instance',
          orgId: '@orgId'
        },
        isArray: true
      }
    });

    returnedObj.instances = $resource('apic/instances/:instance/:action/:type', {}, {
      isLoggedIn: {
        method: 'GET',
        params: {
          instance: '@instance',
          action: 'loggedIn'
        },
        isArray: false
      },
      login: {
        method: 'POST',
        params: {
          instance: '@instance',
          action: 'login'
        },
        isArray: false
      },
      publishProducts: {
        method: 'POST',
        params: {
          instance: '@instance',
          action: 'publish',
          type: 'products'
        },
        isArray: true
      },
      publishRuntime: {
        method: 'POST',
        params: {
          instance: '@instance',
          action: 'publish',
          type: 'runtime'
        },
        isArray: false
      },
      metadata: {
        method: 'GET',
        params: {
          instance: '@instance',
          action: 'metadata'
        },
        isArray: false
      }
    });

    returnedObj.servers = $resource('proxy/orgs/:orgId/apicServers', {}, {
      query: {
        method: 'GET',
        params: {
          orgId: '@orgId'
        },
        isArray: true
      },
      update: {
        method: 'PUT',
        params: {
          orgId: '@orgId'
        },
        isArray: false
      }
    });

    return returnedObj;
  }
  ]);
