// Node module: apim-ui

'use strict';

/* Services */

  angular.module('apim.login')
  .factory('APIc', ['$resource',
    function($resource){
      var returnedObj = {}

      returnedObj.servers = $resource('proxy/orgs/:orgId/apicServers', {}, {
        query: {method: 'GET', params: {orgId: 'localhost'}, isArray: true},
        update: {method: 'PUT', params: {orgId: 'localhost'}, isArray: false}
      });

      returnedObj.defaultServer = $resource('proxy/orgs/:orgId/defaultServer', {}, {
        get: {method: 'GET', params: {orgId: 'localhost'}, isArray: false},
        set: {method: 'PUT', params: {orgId: 'localhost'}, isArray: false}
      });

      return returnedObj;
    }
  ]);

